function addItem(user, pic, text) {
    this.cp().moveBy(pt(0,-2));
    var bounds = pt(0,0).extent(pt(this.getExtent().x - 24, 75)),
        tm = new lively.morphic.Text(bounds, text);
    tm.applyStyle({borderRadius: "8px", fixedHeight: false});
    tm.setPadding(pt(56,25).extent(pt(-56,-20)));
    tm.setFontSize(8);
    (function() {
        tm.fit();
        if (tm.getExtent().y < 72)
            tm.setExtent(tm.getExtent().withY(72))
    }).delay();
    this.addProfileImage(tm, pic);
    this.addUsernameLabel(tm, user);
    this.addMorph(tm, tm);
}

