function addUsernameLabel(entry, username) {
    var bounds = pt(0,0).extent(pt(this.getExtent().x - 24, 20));
    var label = new lively.morphic.Text(bounds, username);
    label.setFill(null);
    label.setBorderColor(null);
    label.setFontSize(8);
    label.toggleBoldness(0, label.textString.length);
    entry.addMorphFront(label);
    label.moveBy(pt(0,3));
}

