function addEnvelop() {
    var insertPosition = pt(1,1);
    var envelopWidth = 30;
    for (var i=0; i < this.submorphs.length; i++) {
        // new envelop position must not be outside queue
        if ( (insertPosition.x + envelopWidth) < this.getExtent().x)
            insertPosition = insertPosition.addPt(pt(envelopWidth,0));
    }
    var envelop = this.partsBinLoader().createMiniEnvelop();
    this.addMorph(envelop);
    envelop.setPosition(insertPosition);
}

