function init(scriptName) {
    this.script = './workers/' + scriptName;
    this.startWorker();
    this.setScriptContent(this.fetchScriptContent());
    this.workItemsInProgress = 0;
    this.indicateIdle();
    this.messages = [];
    this.actors = [];
    this.doNotSerialize = ['attributeConnections', 'workItemsInProgress', 'script', 'isWorking', 'messages', 'target', 'actors', 'worker'];
}

