function startWorker() {
    this.worker = new Worker(this.script);
    var _this = this;
    this.worker.addEventListener("message", function(event) {_this.handleWorkerFinished(event)}, false);
    this.worker.onerror = function(event) {_this.handleWorkerError(event);return;};
}

