function kill() {
    if (this.attributeConnections){
        var connectors = [];
        // find all connectors
        for(var i=0;i < this.attributeConnections.length; i++){
            var connecs = this.attributeConnections;
            if (connecs[i].sourceAttrName === "globalTransform")
                connectors.push(connecs[i]);
        }
        // tell all connected morphs to forget this one here
        for (var j=0; j < connectors.length; j++){
            var connector = connectors[j].targetObj;
            if(connector.fromMorph != this)
                connector.fromMorph.removeActor(this);
            else
                connector.toMorph.removeActor(this);
            connector.remove();
        }
    }
    if (this.worker)
        this.worker.terminate();
    this.remove();
}

