function createActor(actorName) {
    var actor = this.partsBinLoader().createActor(actorName);
    var newActorsPosition = this.getPositionInWorld().addPt(pt(400,0));
    for (var i=0; i < this.actors.length; i++)
        newActorsPosition = newActorsPosition.addPt(pt(0,100));
    actor.setPosition(newActorsPosition);
    this.actors.push(actor);
    actor.registerActor(this);
    this.partsBinLoader().createConnectorFor(this, actor);
}

