function removeActor(actor) {
    if (this.actors){
        var index = this.actors.indexOf(actor);
        if(index!=-1) this.actors.splice(index, 1);
    }
}

532.source:
function registerActor(actor) {
    if(this.actors.indexOf(actor) === -1) {
        this.actors.push(actor);
        if (this.target == null) {
            this.target = actor;
        }
    }
}
