function doAction() {
    var canvas = this.get('Canvas');
    this.world().prompt("Enter pen size", function(str) {
        if (!str) return;
        var size = parseInt(str, 10);
        if (!size) {
            alert('Invalid size!');
            return;
        }
        canvas.setPenSize(size);
    }, canvas.penSize || 3);
}
