function addMorph(morph, optMorphBefore) {
    morph = $super(morph, optMorphBefore);
    morph.unlock();

    if (!morph.backgroundId) { 
        morph.backgroundId = this.newBackgroundID() 
    };

    var unique = false;
    while (!unique) {
        unique = true;
        this.submorphs.each(function (ea) {
            if(ea != morph && 
                ea.backgroundId == morph.backgroundId
            ) {
                unique = false;
                morph.backgroundId = this.newBackgroundID()
            }
        }.bind(this))
    };

    return morph
}

