function applyChangedProperties(oldBgMorph, cardMorph, bgMorph) {
    if(!(oldBgMorph && cardMorph && bgMorph)) return;
    var propertiesToSerialize = this.getPropertiesToSerialize();

    for(var i = 0;i<propertiesToSerialize.length;i++) {
        var prop = propertiesToSerialize[i];
        if ( bgMorph["get"+prop] && 
                (   this.serializeProperty(prop, oldBgMorph) 
                    == 
                    this.serializeProperty(prop, cardMorph)
                )
            ) {
            cardMorph["set"+prop].apply(
                cardMorph, [bgMorph["get"+prop].apply(bgMorph, [])]
            )
        }
    };
}

