function copyMeToCards() {
    // fill bgMorphList
    var bgMorphList = {};
    this.submorphs.each(function (ea) {
        if(!ea.con) {
            bgMorphList[ea.backgroundId] = ea;
        }
    }.bind(this))
    var cardMorphList = {};

    var that = this;
    this.owner.hypercardsArray.each(function (ea) {

        var cardMorphList = {};
        ea.submorphs.each(function (ea) {
            ea.backgroundId ? 
                cardMorphList[ea.backgroundId] = ea : 
                false;
        })

        //Delete deleted morphs on card
        for (var cardkey in cardMorphList) {
            if (cardMorphList.hasOwnProperty(cardkey) && that.oldBgMorphList[cardkey] && !bgMorphList[cardkey]) {
                cardMorphList[cardkey].remove()
                delete cardMorphList[cardkey];
            }
        }

        // Add missing morphs on card
        for (var bgKey in bgMorphList) {
            if(bgMorphList.hasOwnProperty(bgKey) && !cardMorphList[bgKey] && !that.oldBgMorphList[bgKey]) {
                var morphToAdd = bgMorphList[bgKey].copy();
                ea.addMorph(morphToAdd);   
                cardMorphList[ea.backgroundId] = ea;     
            }
        }

        // Copy the properties
        for (var bgKey in bgMorphList) {
            if(bgMorphList.hasOwnProperty(bgKey)) {
                if(cardMorphList[bgKey]) {
                    var bgMorph = bgMorphList[bgKey];
                    var cardMorph = cardMorphList[bgKey];
                    var oldBgMorph = that.oldBgMorphList[bgKey];
                    that.applyChangedProperties(
                        oldBgMorph, cardMorph, bgMorph
                    );
                    that.copyScriptsAndConnections(bgMorph, cardMorph, cardMorphList);
                }
            }
        }

    })
}

