function newBackgroundID() {
    var doubletteFound = true;
    var tempId;
    while(doubletteFound) {
        tempId = Math.random();
        doubletteFound = false;
        this.owner.submorphs.each(function (ea) {
            if (ea.backgroundId == tempId) doubletteFound = true;
        })
    };
    return tempId
}

