function showCard(optFrontCard) {
    this.hypercardsArray.each(function (ea) {
        ea.setVisible(false);
    })
    var frontCard = optFrontCard || this.hypercardsArray[this.selectedCardIndex];
    this.addMorph(frontCard);
    frontCard.setVisible(true);
    this.getControlBoard().selectListAt(this.selectedCardIndex);
}

