}

3540.source:
function addNewCard() {
    var newCard = this.getHyperCardPrototype().copy();
    this.hypercardsArray.each(function (ea) {
	ea.setVisible(false)
    });
    this.hypercardsArray.push(newCard);

    newCard.setExtent(this.getBackground().getExtent());
    this.addMorph(newCard);
    newCard.setPosition(this.getBackground().getPosition());

    this.getControlBoard().updateCardList();
    this.selectedCardIndex = this.hypercardsArray.length-1;
    this.getBackground().copyMeToNewCard(newCard);
    this.showCard();

}

3548.source:
function moveCardDown() {
    var index = this.selectedCardIndex;
    var maxLength = this.hypercardsArray.length;

    if (index>=0 && index<maxLength-1) {
        this.switchCardsAtIndexes(index, index+1);
        this.selectedCardIndex = index+1;
        this.getControlBoard().updateCardList();
        this.showCard();
    }
}

3552.source:
function moveCardUp() {
    var index = this.selectedCardIndex;
    var maxLength = this.hypercardsArray.length

    if (index>0 && index<maxLength) {
        this.switchCardsAtIndexes(index, index-1);
        this.selectedCardIndex = index-1;
        this.getControlBoard().updateCardList();
        this.showCard();
    }
}

3556.source:
function removeCard() {
    var index = this.selectedCardIndex;
    var selectedCard = this.hypercardsArray[index];

    this.hypercardsArray.splice(index,1);
    selectedCard.remove();
    this.getControlBoard().updateCardList();

    if(this.hypercardsArray.length == 0) {
        this.addNewCard();
    }
    this.selectedCardIndex = (index > 0)? index-1 : 0;
    this.showCard();
}

