function renameCard() {
    var selectedCard = this.hypercardsArray[this.selectedCardIndex]
    selectedCard.setName(prompt("Please enter new Name"));
    this.getControlBoard().updateCardList();
}

3568.source:
function switchCardsAtIndexes(x,y) {
    var maxLength = this.hypercardsArray.length
    if (x>=0 && y>=0 && x<maxLength && y<maxLength) {
        var A = this.hypercardsArray;
        A[x] = A.splice(y, 1, A[x])[0];
    }   
}

3572.source:
function cardsText() {
    var morphNames = new Array;
    var args = arguments;
    Properties.own(args).each(function (ea) {
        morphNames.push(args[ea]);
    });

    var textArray = new Array;
    this.hypercardsArray.each(function (card) {
        var cardTextArray = new Array;
        card.submorphs.each(function (ea) {
            morphNames.each(function (name) {
                if(ea.getName && (ea.getName().indexOf(name) >= 0) && (ea.getName().indexOf("Field") >= 0)) {
                    cardTextArray.push(ea.value);
                }
            });
        });
        if (cardTextArray.length != 0) textArray.push(cardTextArray);
    });
    return textArray;   
}

3576.source:
function stepCards(offset) {
    var len = this.hypercardsArray.length;
    this.selectedCardIndex = (this.selectedCardIndex+offset+len)%len
    this.showCard();
}

3580.source:
function find() {
    var morphNames = new Array;
    var args = arguments;
    Properties.own(args).each(function (ea) {
        morphNames.push(args[ea]);
    });

    var cardsArray = new Array;
    this.hypercardsArray.each(function (card) {
        card.submorphs.each(function (ea) {
            morphNames.each(function (name) {
                if(ea.getName && ea.getName().indexOf("Field") >= 0 && ea.value.indexOf(name) >= 0) {
                    cardsArray.push(card);
                }
            });
        });
    });
    return cardsArray.uniq();   
}

3584.source:
function goto(findString) {
    var foundIndex = -1;
    for (var i=0;i<this.hypercardsArray.length;i++){
        if (this.hypercardsArray[i].getName().indexOf(findString) >= 0) {
            foundIndex = i; 
            break;
        }
    }
    if(foundIndex >= 0) {
        this.selectedCardIndex = foundIndex;
        this.showCard();
    }
    else {
        foundIndex = this.hypercardsArray.indexOf(this.find(arguments[0])[0])
        if(foundIndex >= 0) {
            this.selectedCardIndex = foundIndex;
            this.showCard();
        }
        else {
            alertOK("No card found")
        }
    }
}
