1107.source:
function updateLists() {
    this.scriptList.setList(this.sortedScriptNamesOfObj(this.target));
    var scriptListItems = this.scriptList.getList();
    scriptListItems.unshift("-- ALL --");
    this.scriptList.setList(scriptListItems);

    this.connectionList.setList(this.sortedConnectionNamesOfObj(this.target));
    var connectionListItems = this.connectionList.getList();
    connectionListItems.unshift("-- ALL --");
    this.connectionList.setList(connectionListItems);
}

