function printTags(script) {
    if (!script.tags) return "[]";

    var printString = '[';
    for (var i=0; i < script.tags.length; i++) {
        if (i !== 0) printString += ', ';
        printString += '"' + script.tags[i] + '"';
    }
    printString += ']';
    return printString;
}

