function getSiblingsAtCorner(corner) {
    // this.getSiblingsAtCorner("topLeft")
    var p = this.bounds()[corner]()
    var self= this;
    return this.owner.submorphs.select(function(ea) {
        return (ea !== self) && ea.bounds().insetBy(-5).containsPoint(p)
    })
}

