function onMouseDown(evt) {
    if (this.owner != $world) {
        if (this.owner.markedGem == null) {
            this.mark();
        } else if (this.owner.markedGem == this) {
            this.unmark();
        } else {
            console.log(this.neighbours);
            console.log(this.owner.markedGem);
            if (this.hasNeighbour(this.owner.markedGem)) {
                this.switchPositionWith(this.owner.markedGem);
                this.owner.markedGem.unmark();
            }
        }
    }

    console.log(this.gem);
}

