function ruby() {
    var ruby = Morph.makePolygon([pt(15, 9), pt(35, 9), pt(45, 20), pt(25, 41), pt(25, 41), pt(5, 20)], 1, Color.white, Color.red);
    line = Morph.makeLine([pt(5, 20, 0), pt(13, 20)], 2, Color.white);
    ruby.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    line = Morph.makeLine([pt(37, 20, 0), pt(45, 20)], 2, Color.white);
    ruby.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    line = Morph.makeLine([pt(20, 9, 0), pt(13, 20)], 2, Color.white);
    ruby.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    line = Morph.makeLine([pt(13, 20, 0), pt(25, 41)], 2, Color.white);
    ruby.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    line = Morph.makeLine([pt(30, 9, 0), pt(37, 20)], 2, Color.white);
    ruby.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    line = Morph.makeLine([pt(37, 20, 0), pt(25, 41)], 2, Color.white);
    ruby.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    return ruby;
}

7829.source:
function switchPositionWith(gem) {
    var tmpGem = this.gem;
    this.gem = gem.gem
    gem.gem = tmpGem;

    this.updatePicture();
    gem.updatePicture();
    this.tryToStreak();
    gem.tryToStreak();
    this.owner.runFieldPhysics();
}

7833.source:
function addNeighbour(gem, direction) {
    this.neighbours[direction] = gem;
    gem.neighbours[(direction+2) % 4] = this;
}

