9032.source:
function findStreakStart(direction) {
    var neighbour = this.neighbours[direction];
    if(neighbour == null || neighbour.gem != this.gem) {
        var streakDirection = (direction + 2) % 4;
        this.buildStreak(streakDirection, 0);
    }
    else {
        neighbour.findStreakStart(direction);
    }
}

9036.source:
function buildStreak(direction, streakLength) {
    var neighbour= this.neighbours[direction];
    streakLength += 1;
    if(neighbour != null && neighbour.gem == this.gem) {
        streakLength = neighbour.buildStreak(direction, streakLength);
    }

    this.scoreStreak(streakLength);
    return streakLength;
}

