function updatePicture() {
    this.removeAllMorphs();
    if (this.gemTypes().include(this.gem)) {
        this.addMorph(this[this.gem]());
        this.topMorph().setPosition(pt(0, 0));
    } else {
        gem = Morph.makeRectangle(0, 0, 30, 30);
        gem.setFill(Color.random());
        gem.setPosition(pt(10, 10));
        gem.setBorderWidth(0);
        this.addMorph(gem);
    }
    this.topMorph().disableGrabbing(true);
    this.topMorph().disableDropping(true);
}

