    line = Morph.makeLine([pt(30, 10, 0), pt(20, 40)], 1, Color.rgb(215, 255, 230));
    prehnite.addMorph(line);
    line.disableDropping(true);
    line.disableGrabbing(true);
    return prehnite;
}

