14537.source:
function restartIteration() {
    this.iteratorPos = this.fieldHead;
    this.primaryIterDirection = this.iteratorPos.directions.right;
    this.secondaryIterDirection = this.iteratorPos.directions.down;
}

14541.source:
function iterationGetNext() {
    var primaryNext = this.iteratorPos.neighbours[this.primaryIterDirection];
    var secondaryNext = this.iteratorPos.neighbours[this.secondaryIterDirection];

    oldPosition = this.iteratorPos;
    if(primaryNext != null) {
        this.iteratorPos = primaryNext;
    } else {
        this.iteratorPos = secondaryNext;
        this.primaryIterDirection = (this.primaryIterDirection + 2) % 4;
    }
    return oldPosition;
}

14545.source:
function cleanInitialStreaks() {
    this.restartIteration();
    while(this.iterationHasNext()) {
        var gem = this.iterationGetNext();
        gem.tryToStreak();
    }

    this.runFieldPhysics();
}

14549.source:
function deleteGems() {
    this.isStillDirty = false;
    this.restartIteration();
    while(this.iterationHasNext()) {
        var gem = this.iterationGetNext();

        if(gem.shouldBeDeleted) {
            gem.setFill(Color.yellow);
            gem.disappear();
            gem.shouldBeDeleted = false;
            gem.setFill(Color.white);
        }
    }
}

14553.source:
function cleanupDirtyness() {
        this.restartIteration();
        while(this.iterationHasNext()) {
            var gem = this.iterationGetNext();

            if(gem.isDirty) {
                gem.setFill(Color.white);
                this.isStillDirty = true;
                gem.isDirty = false;
                gem.tryToStreak();
            }
        }

        this.restartIteration();
        while(this.iterationHasNext()) {
            var gem = this.iterationGetNext();

            if(gem.shouldBeDeleted) {
                gem.setFill(Color.red);
            }
        }
}

