14732.source:
function newGame() {
    this.clear();
    if (this.gemPrototype == null) {
        this.gemPrototype = $world.loadPartItem("BasicGem", 'PartsBin/groupBejeweled');
    }

    var gemFrame = this.getGemFrame();
    var gemExtent = this.gemPrototype.priorExtent;

    var lastRow = null;
    var isFirstGem = true;

    for (row = 0; row < this.numberOfVerticalGems; row++) {
        var leftNeighbour = null;
        var currentRow = [];
        for (column = 0; column < this.numberOfHorizontalGems; column++) {
            var newGem = this.gemPrototype.copy();
            newGem.becomeRandomGem();
            gemFrame.addMorph(newGem);
            newGem.setPosition(new Point(column * gemExtent.x, row * gemExtent.y));

            if(leftNeighbour != null)
                newGem.addNeighbour(leftNeighbour, newGem.directions.left);

            leftNeighbour = newGem;

            if(lastRow != null)
                newGem.addNeighbour(lastRow[column], newGem.directions.up);
            currentRow[column] = newGem;

            if(isFirstGem) {
                isFirstGem = false;
                this.getGemFrame().fieldHead = newGem;           
            }
        }
        lastRow = currentRow;
    }
    this.getGemFrame().cleanInitialStreaks();
}

14736.source:
function getGemFrame() {
    return this.submorphs.select(function(e) {
        return e.isGemFrame;
    })[0];
}

14740.source:
function clear() {
    this.getGemFrame().submorphs.select(function(e) {
        return e.isGem;
    }).each(function(e) {
        e.remove();
    });
    this.getGemFrame().markedGem = null;
    this.gemPrototype = null;
}
