9455.source:
function performMovement(param) {
    var direction = param.direction;
    var onFinish = param.onFinish;

    var x = this.getPosition().x;
    var y = this.getPosition().y;

    if (direction == undefined) direction = 0; 

    if (direction % 2 == 0) {
        var newPosition = new Point((x + (direction - 1) * 2), y);
        this.setPosition(newPosition);
        if (Math.abs(x - this.originalPosition.x) > this.getExtent().x) {
            this.stopStepping();
            if (onFinish != undefined) onFinish();
            this.setPosition(this.originalPosition)
        }
    } else {
        var newPosition = new Point(x, y + (direction - 2) * 2);
        this.setPosition(newPosition);
        if (Math.abs(y - this.originalPosition.y) > this.getExtent().y) {
            this.stopStepping();    
            if (onFinish != undefined) onFinish();
            this.setPosition(this.originalPosition)
        }
    }

}

