10399.source:
function switchPositionWith(gem) {
    var tmpGem = this.gem;
    this.gem = gem.gem;
    gem.gem = tmpGem;

    this.tryToStreak();
    gem.tryToStreak();
    if(!this.owner.runFieldPhysics())
    {
        tmpGem = this.gem;
        this.gem = gem.gem;
        gem.gem = tmpGem;
        alertOK("Move not allowed!");
    }

    this.updatePicture();
    gem.updatePicture();
}

10403.source:
function addNeighbour(gem, direction) {
    this.neighbours[direction] = gem;
    gem.neighbours[(direction+2) % 4] = this;
}

