11415.source:
function onMouseDown(evt) {
    var markedGem = this.owner.markedGem;
    if (this.owner != $world) {
        if (markedGem == null) {
            this.mark();
        } else if (markedGem == this) {
            this.unmark();
        } else {
            if (this.hasNeighbour(markedGem)) {
                var thisGem = this;
                var thisDirection = this.getDirectionForNeighbour(markedGem);
                var otherDirection = (thisDirection + 2) % 4;
                markedGem.move(otherDirection);
                this.move(thisDirection, function() {
                    thisGem.switchPositionWith(markedGem);
                    markedGem.unmark();
                });
            }
        }
    }

    console.log(this.gem);
}

