function canScoreBySwitchingWith(gem) {
    var tmpGem = this.gem;
    this.gem = gem.gem;
    gem.gem = tmpGem;

    this.tryToStreak();
    gem.tryToStreak();

    var canScore = this.shouldBeDeleted || gem.shouldBeDeleted;

