15792.source:
function canScoreBySwitchingWith(gem) {
    var tmpGem = this.gem;
    this.gem = gem.gem;
    gem.gem = tmpGem;

    this.tryToStreak();
    gem.tryToStreak();

    var canScore = this.shouldBeDeleted || gem.shouldBeDeleted;

    tmpGem = this.gem;
    this.gem = gem.gem;
    gem.gem = tmpGem;

    return canScore;
}

15797.source:
function runFieldPhysics() {
    var isStillDirty = false;
    var wasEverChanged = false;
    var gemCount = 0;
    do {
        isStillDirty = false;
        this.restartIteration();
        while(this.iterationHasNext()) {
            var gem = this.iterationGetNext();

            if(gem.shouldBeDeleted) {
                wasEverChanged = true;
                gemCount += 1;
                gem.disappear();
                gem.shouldBeDeleted = false;
            }
        }
        this.restartIteration();
        while(this.iterationHasNext()) {
            var gem = this.iterationGetNext();

            if(gem.isDirty) {
                isStillDirty = true;
                gem.isDirty = false;
                gem.tryToStreak();
            }
        }
    } while(isStillDirty);
    this.scorePoints(gemCount);

    if(!this.isScoringPossible()) {
        this.owner.gameIsOver();
    }

    return wasEverChanged;
}

15805.source:
function restartIteration() {
    this.iteratorPos = this.fieldHead;
    this.primaryIterDirection = this.iteratorPos.directions.right;
    this.secondaryIterDirection = this.iteratorPos.directions.down;
}

15809.source:
function iterationGetNext() {
    var primaryNext = this.iteratorPos.neighbours[this.primaryIterDirection];
    var secondaryNext = this.iteratorPos.neighbours[this.secondaryIterDirection];

    oldPosition = this.iteratorPos;
    if(primaryNext != null) {
        this.iteratorPos = primaryNext;
    } else {
        this.iteratorPos = secondaryNext;
        this.primaryIterDirection = (this.primaryIterDirection + 2) % 4;
    }
    return oldPosition;
}

15813.source:
