    }

    this.runFieldPhysics();
}

15817.source:
function scorePoints(gemCount) {
    var scoreIncrement = gemCount * 10 * Math.ceil(gemCount / 3);
    if(this.score > 0){
        var scoreMultiplicator = Math.ceil(this.score / 5000);
    } else {
        var scoreMultiplicator = 1;
    }
    this.setScore(this.score + (scoreIncrement * scoreMultiplicator));
}

