15829.source:
function isScoringPossible() {
    var isPossible = false;
    this.restartIteration();
    while(this.iterationHasNext() && !isPossible) {
        var gem = this.iterationGetNext();

        var neighbour = gem.neighbours[gem.directions.down];
        if(neighbour != null)
            isPossible = gem.canScoreBySwitchingWith(neighbour);

        neighbour = gem.neighbours[gem.directions.right];
        if(neighbour != null)
            isPossible = isPossible || gem.canScoreBySwitchingWith(neighbour);
    }

    this.restartIteration();
    while(this.iterationHasNext()) {
        var gem = this.iterationGetNext();
        gem.shouldBeDeleted = false;
    }

    return isPossible;
}

