function getPrototype() {
    var availablePrototypeList = this.prototypeBox.submorphs.select(function(sm) {
        return sm.isGem;
    });

    if (availablePrototypeList.length == 0)
        return $world.loadPartItem("BasicGem", 'PartsBin/groupBejeweled');
    else
        return availablePrototypeList[0];
}
