function migrateOldScriptAnnotations() {
    var target = this.target;

    if (!target) return;

    if (target.scriptAnnotations) {
        Functions.own(target).forEach(function (scriptName){
            var scriptAnnotation = target.scriptAnnotations[scriptName];
            if (!scriptAnnotation) return;
            if (scriptAnnotation.user) {
                target[scriptName].setProperty('user', scriptAnnotation.user);
            }
            if (scriptAnnotation.time) {
                target[scriptName].setProperty('timestamp', scriptAnnotation.time);
            }
            if (scriptAnnotation.categories) {
                target[scriptName].setProperty('tags', scriptAnnotation.categories);
            }
        });
        delete target.scriptAnnotations;
    } 

    Functions.own(target).forEach(function (scriptName) {
        if (target[scriptName].categories) {
            target[scriptName].tags = target[scriptName].categories;
            delete target[scriptName].categories;
        } 
    });

}

