function reset() {
    this.container = this.get("container");
    this.container.setPosition(pt(0,0));
    this.slide = 0;
    var slide = this.build(this.colorSet1());
    this.container.addMorph(slide);
    this.activeSlide = slide;
    this.removeHiddenSlides(this.container);

}

420.source:
function previousSlide() {
    if (this.slide - 1 < 0) {
        this.slide = this.slide + 3;
    }
    this.slide = (this.slide - 1) % 3;
    var previous = this.build(this.slides()[this.slide]);
    previous.setPosition(pt(this.activeSlide.getPosition().x - this.activeSlide.getExtent().x,0));
    this.container.addMorph(previous)
    this.activeSlide = previous;

    this.animateLeft(1000);
}

424.source:
function nextSlide() {
    this.slide = (this.slide+1)%3
    var next = this.build(this.slides()[this.slide]);
    next.setPosition(pt(this.activeSlide.getPosition().x + this.activeSlide.getExtent().x,0));
    this.container.addMorph(next)
    this.activeSlide = next;

    this.animateRight(1000);
}

