function updateTarget(target) {
    this.target = target
    if(target) {
        this.updateSliderColor(target.getBorderColor())
        this.radiusSlider.setValue(target.getBorderRadius())
        this.widthSlider.setValue(target.getBorderWidth())
        this.borderStyleList.setSelection(target.getBorderStyle())
    }
    else {
        this.updateSliderColor(Color.white)
        this.radiusSlider.setValue(0)
        this.widthSlider.setValue(0)
        this.borderStyleList.setSelection("solid")
    }    

}

