function colorForPos(pos) {
    var r = this.shape.getBounds().insetBy(this.getBorderWidth()),
            pos = r.closestPointToPt(pos),
            m = this.activeSlide.submorphs.detect(function(ea) { return ea.bounds().containsPoint(pos) });
            color = m && m.getFill().stops.find(function(ea){return ea.offset == 1}).color;
    return color ? color : Color.black; 
}

