function getColor() {
    var color = this.midColor;
    var alpha = this.get('AlphaSlider').getValue();
    if (this.value < 0.5) {     
        return color.mixedWith(Color.white, 1-(0.5-this.value) * 2).withA(alpha)
    }
    else {     
        return color.mixedWith(Color.black, 1-(this.value-0.5) * 2).withA(alpha)
    }
}

