    if (typeof TapEvents === "undefined") {
        this.worldConnection = connect(lively.morphic.World.current(), 'currentHaloTarget', this, 'updateTarget')
    } else {
        this.worldConnection = connect(lively.morphic.World.current(), 'currentlySelectedMorph', this, 'updateTarget')
    }
}

