function onTouchStart(evt) {
    this.activeBackground();
    evt.stop();
    // var textSelectionRange = this.textMorph.getSelectionRange();
    // if (!textSelectionRange) {
        // this.textMorph.setSelectionRange(this.prevSelectionRange[0], this.prevSelectionRange[1]);
    // } else {
        // this.prevSelectionRange = textSelectionRange;
    // }
    // this.textMorph.doDoit();
    var textSelectionRange = this.textMorph.getSelectionRange();
    if (!textSelectionRange) {
        var selection = this.prevSelectionRange;
    } else {
        this.prevSelectionRange = textSelectionRange;
        var selection = textSelectionRange;
    }
    var string = this.textMorph.textString;
    if (selection[0] == selection[1]) {
        // do stuff on one line
        var start = string.slice(0, selection[0]).lastIndexOf('\n') + 1,
            end = string.indexOf('\n', selection[0]);
        if (start === -1) start = 0;
        if (end === -1) end = string.length;
        selection = [start, end];
    }
    var code = this.textMorph.textString.slice(selection[0], selection[1]);
    this.textMorph.tryBoundEval(code);
    return true;
}

