function onTouchMove(evt) {
    var touchPosition = evt.getPosition();
    var deltaToStart = pt(this.beginTouchPosition.subPt(touchPosition).x,0);
    deltaToStart = deltaToStart.scaleBy($world.getZoomLevel());

    var positionToSet = this.beginMorphPosition.subPt(deltaToStart);
    positionToSet.x = Math.max(positionToSet.x, -this.ownerExtent.x);
    positionToSet.x = Math.min(positionToSet.x, document.documentElement.clientWidth / 3 - this.ownerExtent.x);

    this.owner.setFixedPosition(positionToSet);

    evt.stop();
    return true;
}

