function build(table) {
        var slide = Morph.makeRectangle(0,0,this.getExtent().x,this.getExtent().y)
        slide.setFill(Color.rgba(255,255,255,0));
        slide.setBorderWidth(0);

        var colorNames = this.colorNames(table),
            x = 4, y = 5, // like MacOS colors
            extent = slide.innerBounds().extent().scaleByPt(pt(1/x, 1/y)).subPt(pt(8,8));
            //extent = pt(44,44)
        for (var j = 0; j < y; j++) {
            for (var i = 0; i < x; i++) {
                var idx = j*x+i, // running offset j*x^1 + i*y^0
                    color = CrayonColors[colorNames[idx]],
                    rect = extent.scaleByPt(pt(i, j)).extent(extent),
                    morph = new lively.morphic.Box(rect);
                morph.applyStyle({borderColor: color, borderWidth: 1, borderRadius: 5});
                morph.setFill(this.gradientColor(color));
                morph.moveBy(pt(8*i,8*j));
                morph.ignoreEvents();    
                //morph.layout = {scaleHorizontal: true, scaleVertical: true};
                slide.addMorph(morph);
            }
        }
        //slide.layout = {adjustForNewBounds: true};
        slide.adjustForNewBounds();
        return slide;
}

