    this.addItem("Tools");
    this.addItem("Preferences");
    this.addItem("Debugging");
    this.addItem("Wiki");
    this.addItem("Documentation");
    this.addItem("save world as ...");
    this.addItem("save world");
    this.owner.get("BackButton").setVisible(false);
}

