2205.source:
function stayInBounds() {
    var heightMenu = this.owner.itemList.length*43;
    var heightContainer = this.owner.getExtent().y;
    var maxScroll = heightMenu - heightContainer;

    var yPos = Math.min(0,Math.max(-maxScroll, this.getPosition().y));
    var delta = this.getPosition().y-yPos;

    if(this.velocity*delta > 0) {
        // out of bounds and velocity is in wrong direction
        this.velocity -= delta/15;
    } else if(delta!=0) {
        this.velocity = -delta/15;
    }
//    this.setPosition(pt(0,yPos));

}

2209.source:
function isInBounds() {
    var heightMenu = this.owner.itemList.length * 43;
    var heightContainer = this.owner.getExtent().y;
    var maxScroll = heightMenu - heightContainer;

    var yPos = Math.min(0,Math.max(-maxScroll, this.getPosition().y));
    var delta = this.getPosition().y-yPos;

    return Math.abs(delta)<=0.02;

}

