function pin() {
    console.log("owner before: " + this.owner);
    if (!this.isPinned()) {
        this.pinned = true;
        if (this === $world.touchMenuPrototype) {
            $world.touchMenuPrototype = this.copy();
            $world.touchMenuPrototype.reset();
        }
        var blocker = this.owner;
        this.targetMorph.addMorph(this);
        blocker.remove();
    } else {
        this.pinned = false;
        var blocker = Morph.makeRectangle($world.bounds());
        blocker.applyStyle({
            fill: null,
            opacity: 1,
        });
        $world.addMorph(blocker);
        blocker.addMorph(this);
        connect(blocker, "onTap", blocker, "remove");
        connect(blocker, "onTap", this, "remove");
    }
    console.log("owner after: " + this.owner);
}
