    this._renderContext.shapeNode.style[box_shadow_name] =
        "0px 3px 4px 0px rgba(0,0,0,0.55),"+
        "0px 1px rgba(255,255,255,0.5) inset";
    this.inactiveBackground();
    this.setBorderWidth(1);
    this.setBorderColor(Color.rgb(0,0,0));
    this.setBorderRadius(6);
}

149.source:
function onMouseDown(evt) {
    if(!this.active && evt.isLeftMouseButtonDown()){
        this.activeBackground();
        this.active=true;
    }
    evt.stop();
    var textSelectionRange = this.textMorph.getSelectionRange();
    if (!textSelectionRange) {
        this.textMorph.focus();
        this.textMorph.setSelectionRange(this.prevSelectionRange[0], this.prevSelectionRange[1]);
    } else {
        this.prevSelectionRange = textSelectionRange;
    }
    this.textMorph.doDoit();
    return true;
}

