1792.source:
function handleClick(selection) {
    if (!selection) return;

    if (selection[1] instanceof Array) {
        this.openSubMenu(selection);
        return;
    }

    if (selection[1] instanceof Function && this.owner.targetMorph) {
        var evt = document.createEvent('MouseEvents');
        evt.initMouseEvent(
            'mouseup',
            true,
            true,
            window,
            1,
            0,
            0,
            0,
            0,
            false,
            false,
            false,
            false,
            0,
            null
        );
        lively.morphic.EventHandler.prototype.patchEvent(evt);
        var morph = this.owner;
        var synchron = selection[2] == 'synchron'
        // make the call of the callback function asynchronous, so that complex operations
        // do not block the visual update of the list
        // some operations such as 'save world' need synchron remove of the menu
        if(!morph.isPinned()) {
            if(morph.isFixed) {
                window.setTimeout(function() { 
                    morph.removeFixed(); }, synchron ? 0 : 200);
            } else {
                window.setTimeout(function() { 
                    morph.remove(); }, synchron ? 0 : 200); 
            }
        }
        window.setTimeout(function() { selection[1](evt, morph); }, synchron ? 0 : 50);

    }
}

