function openSuperMenu() {
    this.title = this.titleStack.pop();

    var offset = this.getExtent().x * this.getLevel();

    if (this.getLevel() === 0){
            this.get("BackButton").setVisible(false);
    }

    var that = this;
    var callbackFct = function() {
