function pin() {
    if (!this.isPinned()) {
        this.pinned = true;
        this.get('PinButton').activeBackground();
        var that = this;
        (function () {
            if (that === $world.touchMenuPrototype) {
                $world.touchMenuPrototype = that.copy();
                $world.touchMenuPrototype.reset();
            }
        }).delay(0);
        this.owner.remove(true);
        this.targetMorph.addMorph(this);
        console.log(this.targetMorph + this.owner);
    } else {
        this.pinned = false;
        this.get('PinButton').inactiveBackground();
        $world.addBlockerWith(this);
    }
}

